'''object oriented GoL animation'''#import useful librariesimport numpy as npimport matplotlib.pylab as pltimport matplotlib.animation#import function to advance framefrom update import update_gol#set default parameterssize = 64 #size of gridR = 1 #kernel radiusnp.random.seed(0)class GameOfLifeAnimation:    def __init__(self, size):        '''define the world and figure'''                self.size = size        #create the world as a random grid        self.A = np.random.randint(2, size=(size, size))        self.fig, self.ax = plt.subplots() #figure serves as a container        self.img = self.ax.imshow(self.A, cmap='PuRd', interpolation='nearest',vmin=0,vmax=1)        self.ax.set_title('System')    def update(self, i):        '''update frame'''        self.A = update_gol(self.A)        self.img.set_array(self.A)        return self.img,    def animate(self):        '''simulation as animation'''        ani = matplotlib.animation.FuncAnimation(self.fig, self.update, frames=50, interval=50, blit=True)        ani.save('gol_animation.gif', writer='pillow')                