'''plots of kernel and growth function'''#import useful librariesimport numpy as npimport matplotlib.pylab as pltimport matplotlib.cm as cmdef plots(kernel, R, cmap='PuRd', bar_K=False, save=False):    '''produces 3 subplots:    kernel shape, kernel cross section, growth function'''         fig, ax = plt.subplots(1, 3, figsize=(14,2), gridspec_kw={'width_ratios': [1,1,2]})    '''kernel shape plot'''    ax[0].imshow(kernel.K, cmap=cmap, interpolation="nearest", vmin=0)    ax[0].title.set_text('kernel K')    '''kernel cross-section plot'''    K_size = kernel.K.shape[0]    K_mid = K_size // 2    if bar_K: #plots the crossection as a bar chart        ax[1].bar(range(K_size), kernel.K[K_mid,:], width=1, color=cm.get_cmap(cmap)(0.95))    else: #plots the crossection as a line graph        ax[1].plot(range(K_size), kernel.K[K_mid,:], color=cm.get_cmap(cmap)(0.95))    ax[1].title.set_text('K cross-section')    ax[1].set_xlim([K_mid - R - 3, K_mid + R + 3])    '''growth function'''    x = np.linspace(0, np.sum(kernel.K), 1000)    ax[2].plot(x, kernel.growth(x),color=cm.get_cmap(cmap)(0.95))    ax[2].axhline(y=0, color='grey', linestyle='dotted')    ax[2].title.set_text('growth G')    '''save the figures'''    if save:        plt.savefig('plots.png')        #return fig